func ParseHtmlWithoutTag(content string, tag string) []*SensitiveContentIsHitSchema {
	buffer := bytes.NewBufferString(content)
	document, err := goquery.NewDocumentFromReader(buffer)
	if err != nil {
		return nil
	}
	result := make([]*SensitiveContentIsHitSchema, 0)
	count := int32(0)
	document.Each(func(i int, selection *goquery.Selection) {
		if !selection.Is(tag) {
			count++
			result = append(result, &SensitiveContentIsHitSchema{
				IsHit:   false,
				Content: selection.Text(),
				Index:   count,
			})
		}
	})
	return result
}
