// ResponseAIWorkSummaryMsg AI作品总结消息
type ResponseAIWorkSummaryMsg struct {
	Type    string                 `json:"type"`    // 消息类型 (character/scene)
	WorkID  int64                  `json:"work_id"` // 作品ID
	Data    []interface{}          `json:"data"`    // 任务数据
	Code    int                    `json:"code"`    // 0-成功, 非0-失败
	Message string                 `json:"message"` // 响应消息
	Extra   map[string]interface{} `json:"extra"`   // 额外信息
}

